INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (77, 'Ассорти рыбные из продукции, готовой к употреблению', 'Рыбные продукты аккуратно нарезаны, выложены на тарелку. Распределены равномерно, декорированы зеленью и ингредиентами по рецептуре.', 'Свойственный каждому типу рыбной продукции, ровный, без следов заветривания.', 'Рыбной продукции - плотная, некрошливая, сохраняющая форму.', 'Приятные, свойственные рыбным продуктам, готовым к употреблению, с ароматом копчения. В меру острый, соленый. Без порочащих признаков.', '0', '0', '0', '0', '80', NULL, NULL, NULL, NULL, NULL, NULL, '0.9', '0', '1.3.3.1. - 1.3.3.7.', 'Микробиологические показатели рыбных продуктов соответствуют индексам СанПиН 2.3.2. 1078-01 для определенного продукта в составе ассорти.', 71);
INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (78, 'Икра рыбная соленая, сервированная на льду', 'Икра без следов заветривания, ровной круглой формы, подается в охлажденном виде, в икорницах, на колотом льду.', 'Свойственный данному сорту икры.', 'Икры - плотная, не вязкая, икринки отделяются друг от друга.', 'Приятные, без постороннего и резкого запаха.', '1', '0', '0', '0', '80', NULL, NULL, NULL, NULL, NULL, NULL, '0.9', '0', '1.3.4.4. - 1.3.4.7.', 'Микробиологические показатели икры соленой пром. производства соответствуют индексам СанПиН 2.3.2. 1078-01 для определенного сорта.', 71);
INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (79, 'Карпаччо мясные и рыбные, содержащие компоненты в сыром виде', 'Продукция тонко нарезана, распределена равномерно по тарелке. Сверху основного ингредиента находится соус. Блюдо декорировано ингредиентами по рецептуре.', 'Основного ингредиента - свойственный, без следов заветривания.', 'Основного ингредиента - достаточно плотная, сохраняющяя форму, соуса - жидкая или сметанообразная.', 'Свойственный ингредиентам, в меру острый, соленый, без порочащих признаков.', '0', '0', '0', '0', '80', '1 х 10^5', '0.01', '1.0 <**>', '0.1', '0.1', '25', '0.9', '1', '<*>', '<*> В соответствии с рекомендациями Письма ФС по Надзору в сфере защиты прав потребителей и благополучия человека от 27 декабря 2006 г. N 0100/13930-06-32 «ОБ УСИЛЕНИИ НАДЗОРА ЗА ПРОИЗВОДСТВОМ И ОБОРОТОМ ПРОДУКЦИИ НЕТРАДИЦИОННОГО СОСТАВА». <**> - На основании пунктов СанПиН,  регламентирующих продукты животного происхождения употребляемые в живом виде или после ферментации не обработанного термически сырья (1.3.7.1/3 и 1.1.4.1).  V. parahaemolyticus - не более 10 КОЕ/г. L. monocytogenes в 25 г не допускаются. Дрожжи, КОЕ/г, не более 500, Плесени, КОЕ/г, не более 50 (для изделий с заправками).', 71);
