INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (82, 'Рыба отварная, жареная под маринадом (холодная закуска)', 'Рыба сервирована порционной или мелкокусковой, под маринадом, термическая обработка рыбы - достаточная, однородная. Блюдо декорировано ингредиентами по рецептуре.', 'Свойственный маринаду в составе рецептуры, цвет рыбы на разрезе - светлый, свойственный виду рыбы.', 'Сочная, рыба сохраняет форму, не крошливая.', 'Приятный, в меру острый, соленый, без посторонних примесей и порочащих признаков.', '0', '0', '0', '0', '70', '1 х 10^4', '1.0', NULL, '1.0', '0.1', '25', '0.9', '1.33', '1.9.15.6.', '', 71);
INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (83, 'Салаты из маринованных, квашеных, соленых овощей', 'Все продукты равномерно нарезаны, перемешаны, заправлены. Салат подается на порционной тарелке. Декорирован зеленью и овощами (по рецептуре). Заправка равномерно распределена по компонентам салата. Продукты без признаков заветривания.', 'Свойственный компонентам, входящим в блюдо.', 'Консервированных овощей - достаточно плотная, хрустящая. Овощи и фрукты сохраняют форму, не распадаются.', 'Приятные, свойственные данным компонентам салата, без порочащих признаков.', '0', '1', '0', '0', '70', NULL, '0.1', '0.1', '0.1', '0.1', '25', '0.9', '3', '1.9.15.3.', '', 71);
