INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (87, 'Салаты с добавлением мяса, птицы, рыбы, копченностей (с заправками)', 'Все продукты равномерно нарезаны, перемешаны, заправлены. Салат подается на порционной тарелке. Декорирован зеленью и овощами (по рецептуре). Заправка равномерно распределена по компонентам салата. Продукты без признаков заветривания.', 'Свойственный компонентам, входящим в блюдо.', 'Вареных овощей - мягкая, мяса - мягкая, некрошливая, Свежих и соленых овощей - плотная, хрустящая', 'Приятные, свойственные данным компонентам салата. Без посторонних примесей и порочащих признаков.', '1', '1', '0', '0', '90', '5 х 10^4', '0.1', '0.1', '0.1', '0.1', '25', '0.9', '3', '1.9.15.5.', 'Дрожжи - 500, с консервантами - 200 КОЕ/г, не более; плесени - 50 КОЕ/г, не более.', 71);
INSERT INTO ttk_categories (id, name, view, color, cons, taste, fdry, ffat, fsug, fsalt, gerb, kma, bgkp, ecoli, saur, prot, pato, dryk, smax, sp, rem, parent_id) VALUES (88, 'Студни (заливные) из мяса и птицы', 'Компоненты равномерно распределены, поверхность - ровная, без вмятин и изломов. ', 'Желе - прозрачное, мяса - сероватый.', 'Устойчивая, плотная, не растекающаяся.', 'Отварного мяса, с ароматом специй, в меру острый, соленый. Без посторонних примесей и порочащих признаков.', '0', '0', '0', '0', '80', '1 х 10^4', '0.1', '1.0', '0.1', '0.1', '25', '0.9', '0', '1.9.15.6.', '', 71);
